import classes from "./AddUser.module.css";
import Card from "../ui/card/Card";
import Button from "../ui/button/Button";
import { useState, Fragment, useRef } from "react";
import ErrorModal from "../ui/modal/ErrorModal";
// import Wrapper from "../helpers/Wrapper";

export default function AddUser(props) {
  const [userData, setUserData] = useState({ Name: "", Age: "" });
  const [isValid, setIsValid] = useState(true);
  const [error, setError] = useState();

  const nameRef = useRef();
  const ageRef = useRef();

  const changeHandler = (e) => {
    if (e.target.value.trim().length < 1) {
      // write this code in change and submit
      setIsValid(false);
    }
    setUserData((pre) => ({ ...pre, [e.target.name]: e.target.value })); // you have write ()=>()
  };

  const submitHandler = (e) => {
    e.preventDefault();

    console.log(nameRef.current.value);
    console.log(document.getElementById("user-name"));

    if (userData.Name.trim().length === 0 || userData.Age.trim().length === 0) {
      setIsValid(false);
      setError({
        title: "Error!",
        message:
          "Please enter a valid name and a valid age (No for empty fields!)!",
      });
      return;
    }
    if (+userData.Age < 1) {
      setIsValid(false);
      setError({
        title: "Error!",
        message: "Please enter a valid age!",
      });
      return;
    } else {
      console.log(userData);
      props.onAddUser(userData);
      // Or Pass Name and Age individually;
      // props.onAddUser(userData.Name, userData.Age);
    }
  };

  const errorCloser = () => setError(null);

  return (
    <Fragment>
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={errorCloser}
        />
      )}
      <Card className={classes.input}>
        <form onSubmit={submitHandler}>
          <label className={!isValid ? classes.invalid : ""}>
            Name
            <input
              type="text"
              placeholder="Hisham Sakr"
              id="user-name"
              ref={nameRef}
              name="Name"
              value={userData.Name}
              onChange={changeHandler}
              className={!isValid ? classes.invalid : ""}
            />
          </label>

          <label className={!isValid ? classes.invalid : ""}>
            Age (in years)
            <input
              className={!isValid ? classes.invalid : ""}
              type="number"
              placeholder="40"
              name="Age"
              value={userData.Age}
              id="user-age"
              ref={ageRef}
              onChange={changeHandler}
            />
          </label>

          <Button type="submit">Add User</Button>
        </form>
      </Card>
    </Fragment>
  );
}
